use testdb;

explain select * from Emp where dept = 5;

explain select * from Emp where dept * 1 = 5 order by ename;
explain select * from Emp where mod(dept, 10) = 5 order by ename;
explain select * from Emp where ename like '%천%';
select * from Emp where id > 10 or id < 5;
select * from Emp order by ename limit 10 offset 200;
select * from Emp where dept in (select distinct id from Dept);
select * from Emp e where exists (select distinct id from Dept where id = e.dept);

optimize table Emp;
analyze table Emp;
show index from Emp;
show table status where name in ('Emp', 'User');
select 261 * 62;
select * from User;

drop index idx_Emp_dept_ename on Emp;

