CREATE TABLE Notice(
  id int unsigned not null auto_increment Primary Key,
  createdate timestamp not null default CURRENT_TIMESTAMP comment '작성일',
  workdate timestamp not null default CURRENT_TIMESTAMP
           on Update CURRENT_TIMESTAMP comment '수정일',
  title varchar(255) not null comment '제목',
  writer int unsigned null comment '작성자',
  contents text null comment '내용'
);

ALTER TABLE Notice ADD CONSTRAINT fk_Notice_writer
  FOREIGN KEY (writer) REFERENCES User(id) ON DELETE SET NULL;
  
CREATE FULLTEXT INDEX ft_idx_Notice_title_contents
       on Notice(title, contents);
       
select * from Notice;

insert into Notice(title, contents) values
 ('세종대왕', '조선의 제4대 국왕이다.'),
 ('단군', '단군왕검(檀君王儉)은 한민족의 시조이자 고조선(古朝鮮)의 국조(國祖), 대종교의 시작.'),
 ('정약용', '조선 후기의 문신이자 실학자·저술가·시인·철학자·과학자·공학자이다.'),
 ('계백', '백제 말기의 군인이다.'),
 ('이순신', '조선 중기의 무신이었다. 본관은 덕수(德水), 자는 여해(汝諧), 시호는 충무(忠武).'),
 ('김유신', '신라의 화랑의 우두머리였으며 태대각간(太大角干)이었고 신라에 귀순한 가야 왕족의 후손.');
 
select * from Notice where contents like '%조선%';
select * from Notice where match(title, contents) against('조선');

show index from Notice;

show variables like 'innodb_ft%';

-- 인덱싱 확인
set global innodb_ft_aux_table = 'mydealdb/Notice';

select * from information_schema.innodb_ft_index_table;

SET GLOBAL innodb_optimize_fulltext_only = ON;
OPTIMIZE TABLE Notice;
SET GLOBAL innodb_optimize_fulltext_only = OFF;

select * from Notice where match(title, contents) against('조선');
select * from Notice where match(title, contents) against('위인' with query expansion);

select * from Notice where match(title, contents) against('조선*' IN BOOLEAN MODE);
-- select * from Notice where match(title, contents) against( concat(?, '*') IN BOOLEAN MODE);
select * from Notice where match(title, contents) against('문신* 무신*' IN BOOLEAN MODE);

select * from Notice where match(title, contents) against('문신* 무신* -후기*' IN BOOLEAN MODE);
select * from Notice where match(title, contents) against('문신* 무신* +중기*' IN BOOLEAN MODE);


-- Partition ------------------------------------
use testdb;

create table PartiRangeTest (
  studentno varchar(7) not null,
  enteryear smallint not null,
  studentname varchar(31) not null )
  
  Partition by RANGE(enteryear) (
    partition p1 values less than(2000),
    partition p2 values less than(2010),
    partition p3 values less than MAXVALUE
);

insert into PartiRangeTest(studentno, enteryear, studentname)
  values ('8809080', 1988, '팔팔학번'),
         ('0809080', 2008, '공팔학번'),
         ('1809080', 2018, '일팔학번');

select * from PartiRangeTest;

explain select * from PartiRangeTest where studentno='8809080';
explain select * from PartiRangeTest where enteryear=2008;

select * from information_schema.partitions
 where table_name='PartiRangeTest';
 
create table PartiListTest (
  studentno varchar(7) not null,
  dept varchar(31) not null)
  
  partition by LIST COLUMNS (dept) (
    partition p1 values IN ('컴공', '산공'),
    partition p2 values IN ('사학', '철학')
);

insert into PartiListTest(studentno, dept)
  values ('8809080', '컴공'),
         ('0809080', '산공'),
         ('1809080', '철학');
         
select * from PartiListTest;

explain select * from PartiListTest where dept = '산공';
explain select * from PartiListTest where dept = '사학';

Alter table PartiRangeTest
    REORGANIZE Partition p3 INTO (
        partition p3 values less than (2020),
        partition p4 values less than MAXVALUE
    );

optimize table PartiRangeTest;

select * from information_schema.partitions
 where table_name='PartiRangeTest';

alter table PartiRangeTest
    reorganize partition p1, p2 into (
        partition p1_2 values less than (2010)
    );

explain select * from PartiRangeTest where enteryear=2008;

-- delete from PartiRangeTest where enteryear < 2010;
alter table PartiRangeTest drop partition p1_2;